import React, { useEffect, useState, useCallback } from 'react'
import axios from 'axios'
import Card from '../component/Card'
// import { NavLink } from 'react-router-dom'

const api = {
    key: '85cce4b8a48db0b1c1989db1ce315b26',
    base: 'https://api.openweathermap.org/data/2.5/'
}

// https://api.openweathermap.org/data/2.5/weather?q=Bishkek&appid=85cce4b8a48db0b1c1989db1ce315b26


const Home = ({city}) => {
    const [synoptic, setSynoptic] = useState({})

            const getData = async () => {
                const { data } = await axios(`${api.base}weather?q=${city}&appid=${api.key}`)
                setSynoptic(data)
            }

            console.log(synoptic.main.temp)

        useEffect(() => {
            getData()
        }, [city])

    return (

        <div className='home__wrapper'>
            {
                <Card name={synoptic.name} temp={synoptic?.main?.temp} windSpeed={synoptic?.wind?.speed} windDeg={synoptic?.wind?.deg} />
            }
        </div>
    )
}


export default Home
